import { db } from '../../lib/db'

const saveStatus = async (req, res) => {
  await db.collection('test')
    .add({
      test: 1,
      coordinates: new admin.firestore.GeoPoint(-20.21899, -45.939060)
    })
  console.log(req)
  res.send({ ok: true })
}

export default saveStatus
